import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { emailService } from "./services/emailService";
import { paymentService } from "./services/paymentService";
import { licenseService } from "./services/licenseService";
import { pdfService } from "./services/pdfService";
import { emailVerificationSchema, otpVerificationSchema, checkoutSchema } from "@shared/schema";
import { randomUUID } from "crypto";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Get all products
  app.get("/api/products", async (req, res) => {
    try {
      const products = await storage.getProducts();
      res.json(products);
    } catch (error) {
      console.error("Error fetching products:", error);
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  // Get single product
  app.get("/api/products/:id", async (req, res) => {
    try {
      const product = await storage.getProduct(req.params.id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      console.error("Error fetching product:", error);
      res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  // Send OTP for email verification
  app.post("/api/auth/send-otp", async (req, res) => {
    try {
      const { email } = emailVerificationSchema.parse(req.body);

      // Generate 6-digit OTP
      const otpCode = Math.floor(100000 + Math.random() * 900000).toString();
      const expiresAt = new Date(Date.now() + 10 * 60 * 1000); // 10 minutes

      // Store OTP
      await storage.createOtpCode({
        email,
        code: otpCode,
        expiresAt,
      });

      // Send OTP email
      await emailService.sendOTP(email, otpCode);

      res.json({ 
        message: "OTP sent successfully",
        email,
        // For demo mode, include the OTP in response
        ...(process.env.NODE_ENV === 'development' && { demoOtp: otpCode })
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: error.errors[0].message });
      }
      console.error("Error sending OTP:", error);
      res.status(500).json({ message: "Failed to send verification code" });
    }
  });

  // Verify OTP
  app.post("/api/auth/verify-otp", async (req, res) => {
    try {
      const { email, code } = otpVerificationSchema.parse(req.body);

      const otpRecord = await storage.getValidOtpCode(email, code);
      if (!otpRecord) {
        return res.status(400).json({ message: "Invalid or expired verification code" });
      }

      // Mark OTP as verified
      await storage.markOtpAsVerified(otpRecord.id);

      res.json({ 
        message: "Email verified successfully",
        verified: true 
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: error.errors[0].message });
      }
      console.error("Error verifying OTP:", error);
      res.status(500).json({ message: "Failed to verify code" });
    }
  });

  // Process checkout and payment
  app.post("/api/orders/checkout", async (req, res) => {
    try {
      const checkoutData = checkoutSchema.parse(req.body);

      // Verify email was previously verified (in production, you'd check session/token)
      // For demo, we'll skip this check

      // Get products and calculate totals
      const orderItems = [];
      let subtotal = 0;

      for (const item of checkoutData.items) {
        const product = await storage.getProduct(item.productId);
        if (!product) {
          return res.status(400).json({ message: `Product ${item.productId} not found` });
        }

        const price = parseFloat(product.price);
        const total = price * item.quantity;
        subtotal += total;

        orderItems.push({
          productId: product.id,
          productName: product.name,
          price: product.price,
          quantity: item.quantity,
          total: total.toFixed(2),
        });
      }

      const gst = subtotal * 0.1;
      const totalAmount = subtotal + gst;

      // Generate order ID
      const orderId = `NK2IT-${Date.now()}-${Math.random().toString(36).substr(2, 6).toUpperCase()}`;

      // Create or get customer
      let customer = await storage.getCustomerByEmail(checkoutData.email);
      if (!customer) {
        customer = await storage.createCustomer({
          email: checkoutData.email,
          firstName: checkoutData.billing.firstName,
          lastName: checkoutData.billing.lastName,
          company: checkoutData.billing.company,
          phone: checkoutData.billing.phone,
        });
      }

      // Create order
      const order = await storage.createOrder({
        id: orderId,
        customerId: customer.id,
        email: checkoutData.email,
        status: "processing",
        subtotal: subtotal.toFixed(2),
        gst: gst.toFixed(2),
        total: totalAmount.toFixed(2),
        paymentMethod: "credit_card",
        paymentStatus: "pending",
        billingAddress: checkoutData.billing,
      });

      // Create order items
      const createdItems = [];
      for (const item of orderItems) {
        const orderItem = await storage.createOrderItem({
          orderId: order.id,
          ...item,
        });
        createdItems.push(orderItem);
      }

      // Process payment
      const paymentResult = await paymentService.processPayment({
        cardNumber: checkoutData.payment.cardNumber,
        expiryDate: checkoutData.payment.expiryDate,
        cvv: checkoutData.payment.cvv,
        cardholderName: checkoutData.payment.cardholderName,
        amount: totalAmount,
        currency: 'AUD',
        orderId: order.id,
        customerEmail: checkoutData.email,
        billingAddress: {
          ...checkoutData.billing,
          country: 'Australia',
        },
      });

      if (paymentResult.success) {
        // Update order status
        await storage.updateOrderStatus(order.id, "completed", paymentResult.transactionId);

        // Generate license keys
        const licenseKeysData = [];
        for (const item of createdItems) {
          const keys = licenseService.generateLicenseKeys({
            productId: item.productId,
            productName: item.productName,
            quantity: item.quantity,
            orderId: order.id,
            orderItemId: item.id,
          });

          // Store license keys
          for (const key of keys) {
            await storage.createLicenseKey({
              orderId: order.id,
              orderItemId: item.id || null,
              productId: item.productId,
              licenseKey: key,
            });
          }

          licenseKeysData.push({
            productName: item.productName,
            keys,
          });
        }

        // Send license keys email
        await emailService.sendLicenseKeys(checkoutData.email, order.id, licenseKeysData);

        res.json({
          success: true,
          orderId: order.id,
          transactionId: paymentResult.transactionId,
          licenseKeys: licenseKeysData,
          total: totalAmount,
        });
      } else {
        // Update order status to failed
        await storage.updateOrderStatus(order.id, "failed");

        res.status(400).json({
          success: false,
          message: paymentResult.error || "Payment processing failed",
          orderId: order.id,
        });
      }
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: error.errors[0].message });
      }
      console.error("Error processing checkout:", error);
      res.status(500).json({ message: "Failed to process order" });
    }
  });

  // Get order details
  app.get("/api/orders/:orderId", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.orderId);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      const items = await storage.getOrderItems(order.id);
      const licenseKeys = await storage.getLicenseKeysByOrder(order.id);
      const customer = await storage.getCustomer(order.customerId!);

      res.json({
        order,
        items,
        licenseKeys,
        customer,
      });
    } catch (error) {
      console.error("Error fetching order:", error);
      res.status(500).json({ message: "Failed to fetch order" });
    }
  });

  // Generate and download invoice PDF
  app.get("/api/orders/:orderId/invoice", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.orderId);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      const items = await storage.getOrderItems(order.id);
      const licenseKeys = await storage.getLicenseKeysByOrder(order.id);
      const customer = await storage.getCustomer(order.customerId!);

      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }

      // Group license keys by product
      const licenseKeysByProduct: { [key: string]: string[] } = {};
      for (const license of licenseKeys) {
        const item = items.find(i => i.id === license.orderItemId);
        if (item) {
          if (!licenseKeysByProduct[item.productName]) {
            licenseKeysByProduct[item.productName] = [];
          }
          licenseKeysByProduct[item.productName].push(license.licenseKey);
        }
      }

      const licenseKeysForPdf = Object.entries(licenseKeysByProduct).map(([productName, keys]) => ({
        productName,
        keys,
      }));

      const invoicePdf = pdfService.generateInvoicePdf({
        order,
        customer,
        items,
        licenseKeys: licenseKeysForPdf,
      });

      res.setHeader('Content-Type', 'application/pdf');
      res.setHeader('Content-Disposition', `attachment; filename="NK2IT-Invoice-${order.id}.pdf"`);
      res.send(invoicePdf);
    } catch (error) {
      console.error("Error generating invoice:", error);
      res.status(500).json({ message: "Failed to generate invoice" });
    }
  });

  // Email invoice
  app.post("/api/orders/:orderId/email-invoice", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.orderId);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      const items = await storage.getOrderItems(order.id);
      const licenseKeys = await storage.getLicenseKeysByOrder(order.id);
      const customer = await storage.getCustomer(order.customerId!);

      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }

      // Group license keys by product
      const licenseKeysByProduct: { [key: string]: string[] } = {};
      for (const license of licenseKeys) {
        const item = items.find(i => i.id === license.orderItemId);
        if (item) {
          if (!licenseKeysByProduct[item.productName]) {
            licenseKeysByProduct[item.productName] = [];
          }
          licenseKeysByProduct[item.productName].push(license.licenseKey);
        }
      }

      const licenseKeysForPdf = Object.entries(licenseKeysByProduct).map(([productName, keys]) => ({
        productName,
        keys,
      }));

      const invoicePdf = pdfService.generateInvoicePdf({
        order,
        customer,
        items,
        licenseKeys: licenseKeysForPdf,
      });

      await emailService.sendInvoice(order.email, order.id, invoicePdf);

      res.json({ message: "Invoice sent successfully" });
    } catch (error) {
      console.error("Error sending invoice:", error);
      res.status(500).json({ message: "Failed to send invoice" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type Customer, type InsertCustomer, type Order, type InsertOrder, type OrderItem, type InsertOrderItem, type LicenseKey, type InsertLicenseKey, type Product, type OtpCode, type InsertOtpCode, type Address, type InsertAddress } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Products
  getProducts(): Promise<Product[]>;
  getProduct(id: string): Promise<Product | undefined>;

  // Customers
  getCustomer(id: string): Promise<Customer | undefined>;
  getCustomerByEmail(email: string): Promise<Customer | undefined>;
  createCustomer(customer: InsertCustomer): Promise<Customer>;

  // Addresses
  createAddress(address: InsertAddress): Promise<Address>;

  // Orders
  createOrder(order: InsertOrder): Promise<Order>;
  getOrder(id: string): Promise<Order | undefined>;
  updateOrderStatus(id: string, status: string, paymentReference?: string): Promise<void>;

  // Order Items
  createOrderItem(item: InsertOrderItem): Promise<OrderItem>;
  getOrderItems(orderId: string): Promise<OrderItem[]>;

  // License Keys
  createLicenseKey(license: InsertLicenseKey): Promise<LicenseKey>;
  getLicenseKeysByOrder(orderId: string): Promise<LicenseKey[]>;

  // OTP Codes
  createOtpCode(otp: InsertOtpCode): Promise<OtpCode>;
  getValidOtpCode(email: string, code: string): Promise<OtpCode | undefined>;
  markOtpAsVerified(id: string): Promise<void>;
}

export class MemStorage implements IStorage {
  private products: Map<string, Product> = new Map();
  private customers: Map<string, Customer> = new Map();
  private addresses: Map<string, Address> = new Map();
  private orders: Map<string, Order> = new Map();
  private orderItems: Map<string, OrderItem> = new Map();
  private licenseKeys: Map<string, LicenseKey> = new Map();
  private otpCodes: Map<string, OtpCode> = new Map();

  constructor() {
    this.initializeProducts();
  }

  private initializeProducts() {
    const products: Product[] = [
      {
        id: "endpoint-protection",
        name: "Symantec Endpoint Protection Enterprise",
        description: "Comprehensive endpoint security with advanced threat protection for enterprise environments.",
        price: "89.99",
        features: [
          "Advanced malware protection",
          "Real-time threat detection",
          "Centralized management console",
          "Network and email protection",
          "Device and application control"
        ],
        isActive: true,
        createdAt: new Date(),
      },
      {
        id: "endpoint-complete",
        name: "Symantec Endpoint Security Complete",
        description: "Complete security suite with EDR, threat hunting, and advanced analytics capabilities.",
        price: "149.99",
        features: [
          "Everything in Enterprise +",
          "Endpoint Detection & Response (EDR)",
          "Advanced threat hunting",
          "Behavioral forensics",
          "AI-driven adaptive protection",
          "Global Intelligence Network"
        ],
        isActive: true,
        createdAt: new Date(),
      }
    ];

    products.forEach(product => {
      this.products.set(product.id, product);
    });
  }

  async getProducts(): Promise<Product[]> {
    return Array.from(this.products.values()).filter(p => p.isActive);
  }

  async getProduct(id: string): Promise<Product | undefined> {
    return this.products.get(id);
  }

  async getCustomer(id: string): Promise<Customer | undefined> {
    return this.customers.get(id);
  }

  async getCustomerByEmail(email: string): Promise<Customer | undefined> {
    return Array.from(this.customers.values()).find(customer => customer.email === email);
  }

  async createCustomer(insertCustomer: InsertCustomer): Promise<Customer> {
    const id = randomUUID();
    const customer: Customer = {
      ...insertCustomer,
      id,
      company: insertCustomer.company || null,
      phone: insertCustomer.phone || null,
      createdAt: new Date(),
    };
    this.customers.set(id, customer);
    return customer;
  }

  async createAddress(insertAddress: InsertAddress): Promise<Address> {
    const id = randomUUID();
    const address: Address = {
      ...insertAddress,
      id,
      customerId: insertAddress.customerId || null,
      country: insertAddress.country || "Australia",
    };
    this.addresses.set(id, address);
    return address;
  }

  async createOrder(insertOrder: InsertOrder): Promise<Order> {
    const order: Order = {
      ...insertOrder,
      customerId: insertOrder.customerId || null,
      paymentMethod: insertOrder.paymentMethod || null,
      paymentStatus: insertOrder.paymentStatus || null,
      paymentReference: insertOrder.paymentReference || null,
      createdAt: new Date(),
    };
    this.orders.set(order.id, order);
    return order;
  }

  async getOrder(id: string): Promise<Order | undefined> {
    return this.orders.get(id);
  }

  async updateOrderStatus(id: string, status: string, paymentReference?: string): Promise<void> {
    const order = this.orders.get(id);
    if (order) {
      order.status = status;
      order.paymentStatus = status;
      if (paymentReference) {
        order.paymentReference = paymentReference;
      }
      this.orders.set(id, order);
    }
  }

  async createOrderItem(insertItem: InsertOrderItem): Promise<OrderItem> {
    const id = randomUUID();
    const item: OrderItem = {
      ...insertItem,
      id,
      productId: insertItem.productId || null,
      orderId: insertItem.orderId || null,
    };
    this.orderItems.set(id, item);
    return item;
  }

  async getOrderItems(orderId: string): Promise<OrderItem[]> {
    return Array.from(this.orderItems.values()).filter(item => item.orderId === orderId);
  }

  async createLicenseKey(insertLicense: InsertLicenseKey): Promise<LicenseKey> {
    const id = randomUUID();
    const license: LicenseKey = {
      ...insertLicense,
      id,
      productId: insertLicense.productId || null,
      orderId: insertLicense.orderId || null,
      orderItemId: insertLicense.orderItemId || null,
      status: insertLicense.status || "active",
      createdAt: new Date(),
    };
    this.licenseKeys.set(id, license);
    return license;
  }

  async getLicenseKeysByOrder(orderId: string): Promise<LicenseKey[]> {
    return Array.from(this.licenseKeys.values()).filter(license => license.orderId === orderId);
  }

  async createOtpCode(insertOtp: InsertOtpCode): Promise<OtpCode> {
    const id = randomUUID();
    const otp: OtpCode = {
      ...insertOtp,
      id,
      verified: false,
      createdAt: new Date(),
    };
    this.otpCodes.set(id, otp);
    return otp;
  }

  async getValidOtpCode(email: string, code: string): Promise<OtpCode | undefined> {
    const now = new Date();
    return Array.from(this.otpCodes.values()).find(
      otp => otp.email === email && otp.code === code && otp.expiresAt > now && !otp.verified
    );
  }

  async markOtpAsVerified(id: string): Promise<void> {
    const otp = this.otpCodes.get(id);
    if (otp) {
      otp.verified = true;
      this.otpCodes.set(id, otp);
    }
  }
}

export const storage = new MemStorage();

